mapper.update:
UPDATE MEMBER
SET
MSTATUS=#{mstatus}
WHERE MEMBER_ID=#{memberId}

UPDATE RESTAURANT
SET
RSTATUS=#{rstatus}
WHERE RNO=#{rno}

UPDATE RESTAURANT
SET
ADMINSTATUS=#{rstatus}
WHERE RNO=#{rno}

UPDATE POST
SET
PSTATUS=#{pStatus}
WHERE PNO=#{pNo}

UPDATE PICTURE
SET
PICTURE_STATUS=#{pStatus}
WHERE PNO=#{pNo}

UPDATE POST_COMMENT
SET
CSTATUS=#{ccontent}
WHERE COMMENT_SEQ=#{cno}

UPDATE REPORT
SET
REPORT_STATUS=#{reportStatus}
WHERE RENO=#{reno}
AND REPORT_TYPE='C'

UPDATE REPORT
SET
REPORT_STATUS=#{reportStatus}
WHERE RENO=#{reno}
AND REPORT_TYPE='P'
