mapper.select:
SELECT * FROM MEMBER
WHERE MEMBER_ID = #{memberId}
AND MSTATUS='Y'

mapper.update:
UPDATE MEMBER
PASSWORD = #{password}, NICKNAME = #{nickname}, MCONTENT = #{mcontent}, PHONE = #{phone}, EMAIL = #{email}, ADDRESS = #{address},
MPHOTO = #{mphoto}
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
NICKNAME = #{nickname}, MCONTENT = #{mcontent}, PHONE = #{phone}, EMAIL = #{email}, ADDRESS = #{address},
MPHOTO = #{mphoto}
WHERE MEMBER_ID = #{memberId}
